<span class="line">    hint_str <span class="token operator">=</span> hint_str<span class="token punctuation">:</span><span class="token function">gsub</span><span class="token punctuation">(</span><span class="token string">&quot;%[ban_until%]&quot;</span><span class="token punctuation">,</span> <span class="token function">unix_time_to_date_time_str</span><span class="token punctuation">(</span>ban_until<span class="token punctuation">)</span><span class="token punctuation">)</span></span>
